package com.example.PharmacyApp.controller;import com.example.PharmacyApp.model.API.Order;import com.example.PharmacyApp.model.ResponseMessage;import com.example.PharmacyApp.service.OrderService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("admin/order")public class OrderController {    @Autowired    private OrderService orderService;    @PostMapping("new")    public ResponseEntity<ResponseMessage> createOrder(@RequestBody Order order) throws Exception {        return ResponseEntity.ok(orderService.createOrder(order));    }}