package com.example.PharmacyApp.controller;import com.example.PharmacyApp.model.API.OrderWrapper;import com.example.PharmacyApp.model.Persistance.Orders;import com.example.PharmacyApp.model.ResponseMessage;import com.example.PharmacyApp.service.OrderService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("admin/order")public class OrderController {    @Autowired    private OrderService orderService;    @PostMapping("new")    public ResponseEntity<ResponseMessage> createOrder(@RequestBody Orders order) throws Exception {        return ResponseEntity.ok(orderService.createOrder(order));    }    @RequestMapping(value = "/getOrder/{orderID}", method = RequestMethod.GET)    public ResponseEntity<OrderWrapper> getAllOrder(@PathVariable Integer orderID) {        return ResponseEntity.ok(orderService.getOrder(orderID));    }}