package com.example.PharmacyApp.controller;import com.example.PharmacyApp.model.User;import com.example.PharmacyApp.repository.UserRepository;import com.example.PharmacyApp.service.UserDetailServiceImpl;import com.example.PharmacyApp.service.UserService;import jakarta.servlet.http.HttpServletRequest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.lang.NonNull;import org.springframework.security.core.Authentication;import org.springframework.security.core.context.SecurityContextHolder;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("user")public class UserController {    @Autowired    private UserService userService;    @GetMapping("home")    public ResponseEntity<String> home() {        return ResponseEntity.ok("Welcome user..");    }    @GetMapping("/getUserDetails")    public ResponseEntity<User> getUserDetails(Authentication authentication) {        return ResponseEntity.ok(userService.getUserDetails(authentication.getName()));    }}