package com.example.PharmacyApp.model.Persistance;import com.fasterxml.jackson.annotation.JsonFormat;import jakarta.persistence.Entity;import jakarta.persistence.Id;import lombok.Data;import java.util.Date;@Data@Entitypublic class Orders {    @Id    private int orderID;    private int userID;    private String medication_list;    private float price;    @JsonFormat(shape=JsonFormat.Shape.STRING, pattern="dd-MM-yyyy")    private Date purchased_date;}