package com.example.PharmacyApp.service;import com.example.PharmacyApp.model.Medication;import com.example.PharmacyApp.repository.MedicationRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import static com.example.PharmacyApp.utils.CommonUtils.generateRandomNumber;@Servicepublic class MedicationService {    @Autowired    private MedicationRepository medicationRepository;    public String addMedication(Medication medication) throws Exception {        medication.setMedicationID(generateRandomNumber(7));        medicationRepository.save(medication);        return "Successfully saved";    }    public Medication getMedicationDetailsById(Integer id) {        return medicationRepository.findById(id).orElseThrow();    }    public List<Medication> getAllMedicationDetails() {        return medicationRepository.findAll();    }    public Medication getMedicationDetailsByName(String name) {        return medicationRepository.findByName(name);    }}