package com.example.PharmacyApp.service;import com.example.PharmacyApp.model.User;import com.example.PharmacyApp.repository.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.authentication.AuthenticationManager;import org.springframework.stereotype.Service;@Servicepublic class UserService {    @Autowired    private AuthenticationManager authenticationManager;    @Autowired    private UserRepository userRepository;    public User getUserDetails(String username) {        return userRepository.findByUsername(username).orElseThrow();    }}